import React, { useEffect, useState } from 'react';
import service from './service.js';

function App() {
  const [newTodo, setNewTodo] = useState("");
  const [dueDate, setDueDate] = useState("");  // חדש: שדה עבור תאריך יעד
  const [todos, setTodos] = useState([]);

  async function getTodos() {
    const todos = await service.getTasks();
    setTodos(todos);
  }

  async function createTodo(e) {
    e.preventDefault();
    // אם תאריך יעד הוזן, נוסיף אותו גם
    await service.addTask(newTodo, dueDate);
    setNewTodo(""); // מנקה את השדה
    setDueDate(""); // מנקה את שדה התאריך
    await getTodos(); // מרענן את רשימת המשימות
  }

  async function updateCompleted(todo, isComplete) {
    todo.isComplete = isComplete;
    await service.setCompleted(todo.id, todo);
    await getTodos(); // מרענן את רשימת המשימות
  }

  async function deleteTodo(id) {
    await service.deleteTask(id);
    await getTodos(); // מרענן את רשימת המשימות
  }

  useEffect(() => {
    getTodos();
  }, []);

  return (
    <section className="todoapp">
      <header className="header">
        <h1>todos</h1>
        <form onSubmit={createTodo}>
          <input
            className="new-todo"
            placeholder="Well, let's take on the day"
            value={newTodo}
            onChange={(e) => setNewTodo(e.target.value)}
          />
          <input
            type="datetime-local"  // חדש: קלט עבור תאריך יעד
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
          />
          <button type="submit">Add Task</button>
        </form>
      </header>
      <section className="main" style={{ display: "block" }}>
        <ul className="todo-list">
          {todos.map(todo => {
            return (
              <li className={todo.isComplete ? "completed" : ""} key={todo.id}>
                <div className="view">
                  <input
                    className="toggle"
                    type="checkbox"
                    defaultChecked={todo.isComplete}
                    onChange={(e) => updateCompleted(todo, e.target.checked)}
                  />
                  <label>{todo.name}</label>
                  {/* הצגת תאריך יעד */}
                  {todo.dueDate && <span className="due-date">{new Date(todo.dueDate).toLocaleString()}</span>}
                  <button className="destroy" onClick={() => deleteTodo(todo.id)}></button>
                </div>
              </li>
            );
          })}
        </ul>
      </section>
    </section>
  );
}

export default App;
