import axios from 'axios';

const apiUrl = "https://localhost:5119"; 

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/tasks`)  
    console.log(result.data);

    return result.data;
  },

  addTask: async (name) => {
    console.log('addTask', name);
    try {
      const response = await axios.post(`${apiUrl}/tasks`, { name });
      return response.data;
    } catch (error) {
      console.error('Error adding task:', error);
      return { error: 'Failed to add task' };
    }
  },

  // עדכון סטטוס של משימה (האם היא הושלמה או לא)
  setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete });
    try {
      const response = await axios.put(`${apiUrl}/tasks/${id}`,  isComplete );
      return response.data;
    } catch (error) {
      console.error('Error setting task completion:', error);
      return { error: 'Failed to update task' };
    }
  },

  // מחיקת משימה
  deleteTask: async (id) => {
    console.log('deleteTask', id);
    try {
      const response = await axios.delete(`${apiUrl}/tasks/${id}`);
      return response.data;
    } catch (error) {
      console.error('Error deleting task:', error);
      return { error: 'Failed to delete task' };
    }
  }
};
